<!doctype html><html><head>  <meta charset="utf-8">  <meta name="keywords" content="<?php echo get_post_meta(get_the_ID(), 'keywords', TRUE);?>">  <meta name="description" content="<?php bloginfo('description'); ?>">  <meta name="viewport" content="width=device-width, initial-scale=1.0, viewport-fit=cover"><meta name="robots" content="index, follow">  <link rel="shortcut icon" type="image/png" href="<?php if (get_option('site_icon')){echo get_site_icon_url();}else{ echo get_template_directory_uri().'/favicon.png';}?>"> <title><?php the_title(); ?> - <?php bloginfo('name'); ?></title><?php wp_head(); ?>  <!-- Analytics --> <!-- Analytics END -->  </head><body class="  <?php echo implode(" ",get_body_class()); ?>"><!-- Main container --><div class="page-container">  <?php get_header(); ?><?php if (have_posts()) : while (have_posts()) : the_post(); ?><!-- page-header --><div class="bloc b-parallax l-bloc" id="page-header" <?php if (has_post_thumbnail()){echo 'style="background-image:url(\'' . get_the_post_thumbnail_url() . '\')"';}?>><div class="parallax bg-ink"></div><div class="container bloc-xxl"><div class="row"><div class="col"><h1 class="mg-md text-center post-header text-shadow tc-3633"><?php the_title(); ?></h1></div></div><div class="row"><div class="offset-lg-0 col-lg-12"><ul class="list-unstyled justify-content-center d-flex list-horizontal-layout"><li><span class="far fa-user-circle icon-sm icon-3633"></span> <a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>" class="a-btn text-shadow ltc-3633">By <?php the_author(); ?></a></li><li><span class="far fa-calendar icon-sm icon-3633"></span> <a href="<?php echo get_permalink(); ?>" class="a-btn text-shadow ltc-3633"><?php echo get_the_date(); ?></a></li><li><span class="far fa-comment-alt icon-sm icon-3633"></span><a href="#" class="a-btn text-shadow ltc-3633" data-scroll-speed="1000" onclick="scrollToTarget('#comment-section',this)">&nbsp;<?php echo get_comments_number(); ?> Comment<?php if (get_comments_number() != 1){echo "s";} ?></a></li></ul></div></div></div></div><!-- page-header END --><!-- content-section --><div class="bloc l-bloc" id="content-section"><div class="container pb-0 bloc-lg"><div class="row"><div class="col-lg-8 offset-lg-2"><div><div><?php the_content(); ?></div></div><div class="row"><div class="col"><h6 class="mg-md float-lg-none float-none text-left"><span class="fa fa-tag"></span>&nbsp;<?php echo get_post_tag_links(', ');?></h6><?php if (is_user_logged_in()) : ?><div class="text-lg-left"><span class="far fa-edit icon-188"></span><a href="<?php echo get_edit_post_link( get_the_ID()); ?>" class="a-btn text-lg-left ltc-188">Edit Post</a></div><?php endif; ?></div><div class="col"><h6 class="mg-md text-lg-right"><?php echo the_category(', ') ;?></h6></div></div><div class="divider-h"></div></div><div class="offset-lg-2 col-lg-1 col-md-2 col-sm-2"><a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>"><img src="<?php echo get_template_directory_uri(); ?>/img/lazyload-ph.png" data-src="<?php echo get_avatar_url(get_the_author_meta('ID'),array('size' => 200));?>" class="img-fluid rounded-circle mx-auto d-block lazyload" alt="placeholder user" width="68" height="68"></a></div><div class="offset-lg-0 col-lg-7 col-md-10 col-sm-10"><h5 class="tc-3379 mg-clear">Author:&nbsp;<?php the_author(); ?></h5><p class="mg-clear"><?php echo get_the_author_meta( 'description', get_the_author_meta('ID') ); ?></p><a href="<?php echo get_author_posts_url(get_the_author_meta('ID')); ?>" class="a-btn ltc-188 float-lg-none a-block">View all posts by <?php the_author(); ?></a></div><div class="offset-lg-2 col-lg-8"><div class="divider-h"></div></div></div></div></div><!-- content-section END --><!-- comment-section --><div class="bloc l-bloc" id="comment-section"><div class="container bloc-md"><div class="row"><div class="col-lg-8 offset-lg-2"><div><div class="wp-comments-container wp-comments-round-avatars"><?php comments_template(); ?